Problem.11654 아스키 코드
#include &lt;stdio.h&gt; int main(){ char a; while((a=getchar())!=EOF){ if(a!=10) printf(&#34;%d\n&#34;,a); } } Problem.10809 10809번알파벳 찾기
#include &lt;stdio.h&gt; #include &lt;stdlib.h&gt; #include &lt;string.h&gt; int main(){ char comp_alpha[26]=&#34;abcdefghijklmnopqrstuvwxyz&#34;; int comp_location[26]={0,}; char buf[110]={&#39;\0&#39;,}; for(int i=0;i&lt;26;i++){ comp_location[i]=-1; } fgets(buf,sizeof(buf),stdin); //a~z까지 입력된단어에서의 처음 위치를 찾는다 for(int i=0;i&lt;26;i++){ for(int j=0;j&lt;strlen(buf);j++){ if(buf[j]==comp_alpha[i]){ comp_location[i]=j; break; } } } for(int i=0;i&lt;26;i++){ printf(&#34;%d &#34;,comp_location[i]); } puts(&#34;&#34;); } 알파벳은 26개이다 각 단어에서 알파벳이 처음 등장 하는 위치를 다른 배열에 저장한다.
반복문에서 처음 위치를 찾으면 break 되기 때문에
중복이 되서 그다음 위치가 저장될일이 없다.
##Problem.2675 문자열 반복
#include &lt;stdio.h&gt; #include &lt;stdlib.h&gt; #include &lt;string.h&gt; void QR_code(); int main(){ int N; char buf[24]={&#39;\0&#39;,}; //	문자는 20글자 앞에 반복횟수 N 공백 까지 22에 마지막 엔터 23으로 되야 하는데 안되서 24로 잡음 scanf(&#34;%d&#34;,&amp;N); while(getchar()!=10); for(int i=0;i&lt;N;i++){ fgets(buf,sizeof(buf),stdin); QR_code(buf); puts(&#34;&#34;); memset(buf,&#39;\0&#39;,strlen(buf)); } } void QR_code(char *q){	for(int i=2;i&lt;strlen(q)-1;i++){ for(int j=0;j&lt;(*q)-48;j++){ printf(&#34;%c&#34;,*(q+i)); }	} } ##Problem.1157 단어 공부
#include &lt;stdio.h&gt; #include &lt;string.h&gt; int main(){ int alpha[26]={0,}; //	정수형 배열을 쓰는 이유는 단어의 길이가 1000000이기 때문에 반복된 //	알파벳의 횟수가 127번을 넘어가면 CHAR형의 범위를 넘어버린다. char temp; int Max_comp=0; int comp2=0; int comp1=0; //각 단어가 들어왔을때 대소문자 상관없이 각 문자 마다 카운트 한다. while((temp=getchar())!=10){ if(temp&gt;64&amp;&amp;temp&lt;91){ //대문자 일때 alpha[temp-65]+=1; } else{ //	소문자 일때 alpha[temp-97]+=1; } } //가장 많이 사용되 문자 찾는과정 for(int i=0;i&lt;26;i++){ if(alpha[i]!=0){ Max_comp=(Max_comp&gt;alpha[i]?Max_comp:alpha[i]); } } for(int j=0;j&lt;26;j++){ if(Max_comp==alpha[j]){ comp1=j; comp2++;//가장 많이 사용된 문자의 갯수를 저장	} } if(comp2&gt;1){ printf(&#34;?\n&#34;); //가장 많이 사용한 문자가 2개이상 일때는 ?를 출력 } else printf(&#34;%c\n&#34;,comp1+65); //가장 많이 사용한 문자가 2개 미만일때는 해당 문자 출력 } 가장 많이 사용된 문자의 갯수를 문자별로 배열에 저장하고, 가장많이 사용된 문자를 찾아 다시 비교해 주면된다. 비교 하는 과정에서 가장큰 문자는 1번만 나와야하고 2번 이상 나온다면 &ldquo;?&ldquo;를 출력해주어야 한다.
##Problem.1316 그룹 단어 체커
#include &lt;stdio.h&gt; #include &lt;string.h&gt; int checker(); int main(){ int N; char buf[101]={&#39;\0&#39;,}; int result=0; scanf(&#34;%d&#34;,&amp;N); for(int i=0;i&lt;N;i++){ scanf(&#34;%s&#34;,buf); result+=checker(buf); } printf(&#34;%d\n&#34;,result); } int checker(char *q){ char alpha[101]={&#39;\0&#39;}; int duplicate_count=0; for(int i=0;i&lt;strlen(q);i++){ if(alpha[duplicate_count]==0&amp;&amp;alpha[duplicate_count-1]!=(*(q+i)))	{//알파벳 배열에 연속된 같은 문자의 중복을 피하고 저장 alpha[duplicate_count]=(*(q+i)); duplicate_count++; } } for(int j=0;j&lt;strlen(alpha);j++){ for(int k=j+1;k&lt;strlen(alpha);k++){ if(alpha[j]==alpha[k]){ //j번째의 문자가 j+1번째부터 배열의 길이 까지 //한번이라도 나오면 연속단어가 성립 안됨 return 0; } } } return 1; } 입력 받은 문장에서 단어를 체크하는 함수로 입력받은 문장의 주소 전달. 문장에서 연속된 문자의 중복을 제거하면서 한번씩만 배열에 저장 이후 N번째의 문자가 N+1번째부터 끝까지 한번이라도 나온다면 연속문자가 아님.
##Problem.2908 상수
#include &lt;stdio.h&gt; #include &lt;stdlib.h&gt; #include &lt;string.h&gt; int main(){ char buf[8]={0,}; int start_num=2; fgets(buf,sizeof(buf),stdin); for(int i=3;0&lt;i;i--){ if(buf[i-1]&gt;buf[i+3]&amp;&amp;(buf[i-1]!=buf[i+3])){ //각 숫자의 가장 뒷부분을 비교 여기서 큰쪽이 큰수가 된다. //같은 수인걸 조건으로 배제해야 한다. start_num=2; break; } else{ if(buf[i-1]&lt;buf[i+3]&amp;&amp;(buf[i-1]!=buf[i+3])){ start_num=6; break; } }	} //만약 두수가 111 111이라면 같은 수이므로 위의 반복문을 돌고 나서 //start_num에는 아무 변화가 없을것이다. 그래서 초기값으로 2를 넣어줌 for(int j=0;j&lt;3;j++){ printf(&#34;%c&#34;,buf[start_num-j]); //두 수를 비교해서 큰숫자가 판명 나면 그 숫자의 시작 번호를 받아와 거꾸로 출력 } puts(&#34;&#34;); } ##Problem.5622 다이얼
#include &lt;stdio.h&gt; #include &lt;string.h&gt; int main(){ char buf[16]={&#39;\0&#39;,}; int result=0; scanf(&#34;%s&#34;,buf); for(int i=0;i&lt;strlen(buf);i++){ if(buf[i]&gt;=65&amp;&amp;buf[i]&lt;=67) result+=3;	else if(buf[i]&gt;=68&amp;&amp;buf[i]&lt;=70) result+=4; else if(buf[i]&gt;=71&amp;&amp;buf[i]&lt;=73) result+=5; else if(buf[i]&gt;=74&amp;&amp;buf[i]&lt;=76) result+=6; else if(buf[i]&gt;=77&amp;&amp;buf[i]&lt;=79) result+=7; else if(buf[i]&gt;=80&amp;&amp;buf[i]&lt;=83) result+=8; else if(buf[i]&gt;=84&amp;&amp;buf[i]&lt;=86) result+=9; else	result+=10; } printf(&#34;%d\n&#34;,result); } 그냥 조건에 맞춘 노가다 인듯 싶다&hellip;중요한건 다이얼에서 1과 0은 입력이 없다고 보면 된다. 2~9까지의 수만 쓰인다 생각하고 풀면됨
##Problem.2941 크로아티아 알파벳
#include &lt;stdio.h&gt; #include &lt;string.h&gt; int main(){ char buf[101]={&#39;\0&#39;,}; int croatia=0; scanf(&#34;%s&#34;,buf); for(int i=0;i&lt;strlen(buf);i++){ if(buf[i]==&#39;c&#39;&amp;&amp;(buf[i+1]==&#39;=&#39;||buf[i+1]==&#39;-&#39;)){ croatia++; i+=1; } else if(buf[i]==&#39;d&#39;){ if(buf[i+1]==&#39;z&#39;&amp;&amp;buf[i+2]==&#39;=&#39;){ croatia++; i+=2; } else if(buf[i+1]==&#39;-&#39;){ croatia++; i+=1; } else{ croatia++; } } else if(buf[i]==&#39;l&#39;&amp;&amp;buf[i+1]==&#39;j&#39;){ croatia++; i+=1; }	else if(buf[i]==&#39;n&#39;&amp;&amp;buf[i+1]==&#39;j&#39;){ croatia++; i+=1; }	else if(buf[i]==&#39;s&#39;&amp;&amp;buf[i+1]==61){ croatia++; i+=1; } else if(buf[i]==&#39;z&#39;&amp;&amp;buf[i+1]==&#39;=&#39;){ croatia++; i+=1; } else{ croatia++; } } printf(&#34;%d\n&#34;,croatia); } 이것도 조건에 맞춘 노가다 인듯 싶다.

Math Problem Problem.1978 - Tag[수학] 소수찾기
소수찾기 소수는 sqrt(N)까지만 검색하면 된다. 2~sqrt(N)까지 나누어 떨어지는 수가 없다면 소수가 보장되기 때문임. #include &lt;stdio.h&gt; #include &lt;math.h&gt; int is_prime(); int main(){ int N,input,count=0,prime=1; scanf(&#34;%d&#34;,&amp;N); for (int i = 1; i &lt;= N; ++i) { // input=i; scanf(&#34;%d&#34;,&amp;input); if(is_prime(input)==1) count++; } printf(&#34;%d\n&#34;,count); } int is_prime(int num){ if(num==1) return 0; for (int j = 2; j &lt;= sqrt(num); ++j) { if(num%j==0){ return 0; } } return 1; } 에라토네스의 체 알고리즘 /* 에라토스테네스의 체 알고리즘을 기준으로 찾는다. 2부터 소수를 구하고자 하는 구간의 모든 수를 나열한다. 그림에서 회색 사각형으로 두른 수들이 여기에 해당한다. 2는 소수이므로 오른쪽에 2를 쓴다. (빨간색) 자기 자신을 제외한 2의 배수를 모두 지운다. 남아있는 수 가운데 3은 소수이므로 오른쪽에 3을 쓴다. (초록색) 자기 자신을 제외한 3의 배수를 모두 지운다. 남아있는 수 가운데 5는 소수이므로 오른쪽에 5를 쓴다. (파란색) 자기 자신을 제외한 5의 배수를 모두 지운다. 남아있는 수 가운데 7은 소수이므로 오른쪽에 7을 쓴다. (노란색) 자기 자신을 제외한 7의 배수를 모두 지운다. 위의 과정을 반복하면 구하는 구간의 모든 소수가 남는다. */ #include &lt;stdio.h&gt; #include &lt;string.h&gt; #include &lt;stdlib.h&gt; int main(){ int N,count=1; scanf(&#34;%d&#34;,&amp;N); char *arr=(char*)malloc(sizeof(char)*N); memset(arr,1,sizeof(char)*N); puts(arr); for (int i = 2; (i*i)&lt;= N; ++i) //루트 N까지만 체크 { if(arr[i]==1){ for (int j = i*i; j &lt; N; j+=i) { arr[j]=0;//1 is prime 0 is not prime } } } for (int i = 2; i &lt; N; ++i) { if(arr[i]==1){ printf(&#34;%d &#34;,i); if(count%10==0) puts(&#34;&#34;); count++; } } free(arr); } Pascal Triangle 파스칼 삼각형
#include &lt;stdio.h&gt; #include &lt;stdlib.h&gt; int main(){ int N; scanf(&#34;%d&#34;,&amp;N); int **row; row=(int**)malloc(sizeof(int*)*N); for (int i = 0; i &lt; N; ++i) { row[i]=(int*)malloc(sizeof(int)*((N*2)-1)); } for (int i = 0; i &lt; N; ++i) { for (int j = 0; j &lt; i+1; ++j) { if(j==0||j==i){ *(*(row+i)+(N-1+(j*2)-i))=1; } else{ *(*(row+i)+(N-1-i+(j*2)))=(*(*(row+i-1)+(N-1-i-1+(j*2))))+(*(*(row+(i-1))+(N-1-i+(j*2)+1))); } } } for (int i = 0; i &lt; N; ++i) { for (int q = 0; q &lt; (N*2)-1; ++q) { if(*(*(row+i)+q)%2==1) // if(*(*(row+i)+q)!=0) printf(&#34;%d&#34;,*(*(row+i)+q)); else{ printf(&#34; &#34;); } } free(*(row+i)); puts(&#34;&#34;); } } 모든 내용은 Git Hub에도있습니다.
